// SPDX-License-Identifier: GPL-3.0-or-later
pragma solidity ^0.7.6;
pragma abicoder v2;

import '../Shields.sol';
import '../interfaces/IFieldSVGs.sol';
import '../interfaces/IHardwareSVGs.sol';
import '../interfaces/IFrameSVGs.sol';
import '../interfaces/IBuildPassSVGs.sol';

/// @dev Generate Customizable Shields
contract ShieldsTest is Shields {
    constructor(
        string memory name_,
        string memory symbol_,
        IShieldDescriptor _shieldDescriptor,
        address _makerPassRecipient,
        address _granteePassReceipient
    ) Shields(name_, symbol_, _shieldDescriptor, _makerPassRecipient, _granteePassReceipient) {}

    function getNextId() external view returns (uint256) {
        return _nextId;
    }

    function setNextId(uint256 nextId) external {
        _nextId = nextId;
    }
}

contracts/Shields.sol.content:
// SPDX-License-Identifier: GPL-3.0-or-later
pragma solidity ^0.7.6;
pragma abicoder v2;

import './interfaces/ICategories.sol';
import './interfaces/IShields.sol';
import './interfaces/IShieldDescriptor.sol';
import './interfaces/IFieldSVGs.sol';
import './interfaces/IHardwareSVGs.sol';
import './interfaces/IFrameSVGs.sol';
import './interfaces/IHardwareGenerator.sol';
import './interfaces/IFrameGenerator.sol';
import './interfaces/IBuildPassSVGs.sol';
import '@openzeppelin/contracts/token/ERC721/ERC721.sol';
import '@openzeppelin/contracts/access/Ownable.sol';
import '@openzeppelin/contracts/utils/Strings.sol';
import 'base64-sol/base64.sol';

/// @dev Generate Customizable Shields
contract Shields is ERC721, IShields, Ownable {
    using Strings for uint8;

    event ShieldBuilt(
        uint256 tokenId,
        uint16 field,
        uint16 hardware,
        uint16 frame,
        uint24[4] colors,
        MintPass mintPass
    );

    IShieldDescriptor public immutable shieldDescriptor;
    address public immutable makerPassRecipient;
    address public immutable granteePassReceipient;

    uint256 constant makerPassThreshold = 5;
    uint256 constant granteePassThreshold = 405;
    uint256 constant preMintThreshold = 505;
    uint256 constant standardMintMax = 5000;

    uint256 public constant mythicFee = 0.1 ether;
    uint256 public constant publicMintPrice = 0.1 ether;
    bool public publicMintActive = false;

    bool premintedMaker;
    bool premintedGrantee;
    bool premintedStandard;
    uint256 internal _nextId;

    mapping(uint256 => Shield) private _shields;
    // transient variable that's immediately cleared after checking for duplicate colors
    mapping(uint24 => bool) private _checkDuplicateColors;
    // record shieldHashes so that duplicate shields cannot be built
    mapping(bytes32 => bool) public shieldHashes;

    modifier canPremintMaker() {
        require(premintedMaker, 'Shields: maker already preminted');
        _;
    }

    modifier canPremintGrantee() {
        require(premintedMaker, 'Shields: grantee already preminted');
        _;
    }

    modifier canPremintStandard() {
        require(premintedMaker, 'Shields: standard already preminted');
        _;
    }

    modifier publicMintIsActive() {
        require(publicMintActive, 'Shields: Public mint not active yet');
        _;
    }

    modifier publicMintPaid() {
        require(msg.value == publicMintPrice, 'Shields: Insufficient mint fee');
        _;
    }

    constructor(
        string memory name_,
        string memory symbol_,
        IShieldDescriptor _shieldDescriptor,
        address _makerPassRecipient,
        address _granteePassReceipient
    ) ERC721(name_, symbol_) Ownable() {
        shieldDescriptor = _shieldDescriptor;
        makerPassRecipient = _makerPassRecipient;
        granteePassReceipient = _granteePassReceipient;

        _nextId = preMintThreshold + 1;
    }

    function premintMaker() external canPremintMaker {
        for (uint256 i = 1; i <= makerPassThreshold; i++) {
            _mint(makerPassRecipient, i);
        }
        premintedMaker = true;
    }

    function premintGrantee() external canPremintGrantee {
        for (uint256 j = makerPassThreshold + 1; j <= granteePassThreshold; j++) {
            _mint(granteePassReceipient, j);
        }
        premintedGrantee = true;
    }

    function premintStandard() external canPremintStandard {
        for (uint256 k = granteePassThreshold + 1; k <= preMintThreshold; k++) {
            _mint(makerPassRecipient, k);
        }
        premintedStandard = true;
    }

    // ============ OWNER INTERFACE ============

    function collectFees() external onlyOwner {
        (bool success, ) = payable(msg.sender).call{value: address(this).balance}(new bytes(0));
        require(success, 'Shields: ether transfer failed');
    }

    function setPublicMintActive() external onlyOwner {
        publicMintActive = true;
    }

    // ============ PUBLIC INTERFACE ============

    function mint(address to) external payable publicMintIsActive publicMintPaid {
        uint256 tokenId = _nextId++;
        require(tokenId <= standardMintMax, 'Shields: minting closed');
        _mint(to, tokenId);
    }

    function build(
        uint16 field,
        uint16 hardware,
        uint16 frame,
        uint24[4] memory colors,
        uint256 tokenId
    ) external payable override {
        require(msg.sender == ERC721.ownerOf(tokenId), 'Shields: only owner can build NFT');
        require(!_shields[tokenId].built, 'Shields: shield already built');
        validateColors(colors, field);

        Shield memory shield;
        shield.built = true;
        shield.field = field;
        shield.hardware = hardware;
        shield.frame = frame;
        shield.colors = colors;
        shield.mintPass = calculateMintPass(tokenId);

        bytes32 shieldHash = keccak256(abi.encode(field, hardware, colors));
        require(!shieldHashes[shieldHash], 'Shields: unique shield already minted');

        uint256 fee;
        ICategories.Categories fieldType = shieldDescriptor
            .fieldGenerator()
            .generateField(shield.field, shield.colors)
            .fieldType;
        ICategories.Categories hardwareType = shieldDescriptor
            .hardwareGenerator()
            .generateHardware(shield.hardware)
            .hardwareType;
        uint256 frameFee = shieldDescriptor.frameGenerator().generateFrame(shield.frame).fee;
        if (fieldType == ICategories.Categories.MYTHIC) {
            fee += mythicFee;
        }
        if (hardwareType == ICategories.Categories.MYTHIC) {
            fee += mythicFee;
        }
        fee += frameFee;

        require(msg.value == fee, 'Shields: insufficient building fee');

        _shields[tokenId] = shield;
        shieldHashes[shieldHash] = true;

        emit ShieldBuilt(tokenId, field, hardware, frame, colors, calculateMintPass(tokenId));
    }

    function tokenURI(uint256 tokenId) public view override(ERC721) returns (string memory) {
        require(_exists(tokenId), 'Shields: URI query for nonexistent token');
        Shield memory shield = _shields[tokenId];

        if (!shield.built) {
            return shieldDescriptor.generateBuildPassURI(calculateMintPass(tokenId));
        } else {
            return shieldDescriptor.generateShieldURI(shield);
        }
    }

    function shields(uint256 tokenId)
        external
        view
        override
        returns (
            uint16 field,
            uint16 hardware,
            uint16 frame,
            uint24 color1,
            uint24 color2,
            uint24 color3,
            uint24 color4,
            MintPass mintPass
        )
    {
        require(_exists(tokenId), 'Shield: tokenID does not exist');
        Shield memory shield = _shields[tokenId];
        return (
            shield.field,
            shield.hardware,
            shield.frame,
            shield.colors[0],
            shield.colors[1],
            shield.colors[2],
            shield.colors[3],
            shield.mintPass
        );
    }

    // ============ INTERNAL INTERFACE ============

    function calculateMintPass(uint256 tokenId) internal pure returns (MintPass) {
        if (tokenId <= makerPassThreshold) {
            return MintPass.MAKER;
        } else {
            return MintPass.STANDARD;
        }
    }

    function validateColors(uint24[4] memory colors, uint16 field) internal {
        if (field == 0) {
            checkExistsDupsMax(colors, 1);
        } else if (field <= 242) {
            checkExistsDupsMax(colors, 2);
        } else if (field <= 293) {
            checkExistsDupsMax(colors, 3);
        } else {
            checkExistsDupsMax(colors, 4);
        }
    }

    function checkExistsDupsMax(uint24[4] memory colors, uint8 nColors) private {
        for (uint8 i = 0; i < nColors; i++) {
            require(_checkDuplicateColors[colors[i]] == false, 'Shields: all colors must be unique');
            require(shieldDescriptor.fieldGenerator().colorExists(colors[i]), 'Shields: color does not exist');
            _checkDuplicateColors[colors[i]] = true;
        }
        for (uint8 i = 0; i < nColors; i++) {
            _checkDuplicateColors[colors[i]] = false;
        }
        for (uint8 i = nColors; i < 4; i++) {
            require(colors[i] == 0, 'Shields: max colors exceeded for field');
        }
    }
}

